import React, { useCallback, useEffect, useState } from "react";
import {useDispatch, useSelector } from 'react-redux';
import { BrowserRouter, Route, Routes } from "react-router-dom";
import axios from "axios";

import API_URL from '../config';
import {useAuth} from "../Hooks/useAuth";
import AuthContext from "../Contexts/AuthContext";

import Home from "./Home";
import SignIn from "../Auth/SignIn";
import SignUp from "../Auth/SignUp";
import Header from "../Layouts/Header";
import Footer from "../Layouts/Footer";

import {
  fetchArticles,
  updateArticle,
  fetchCategories,
  setSearchByText,
  fetchTotal
} from '../Redux/actions';

function App() {
  axios.defaults.withCredentials = true;  
  const {userData} = useAuth();
  const [authData, setAuthData] = useState({signedIn: userData.signedIn, user: userData.user});

  const dispatch = useDispatch();
  const articles = useSelector((state) => state.articles);
  const categories = useSelector((state) => state.categories);
  const textSearch = useSelector((state) => state.textSearch);
  const total = useSelector((state) => state.total);




  return (
      <AuthContext.Provider value={{authData, setAuthData }}>
        <Routes>
          <Route
            path="/"
            element={
              <>
                <Header
                  categories={categories}
                  fetchNewsByCategory={fetchNewsByCategory}
                  searchByText={[setSearchByText, searchByText]}
                />
                <Home articles={articles} totalItems={total} />
                <Footer />
              </>
            }
          />
          <Route path="/signIn" element={<SignIn />} />
          <Route path="/signUp" element={<SignUp />} />
          {/* Add your other routes here */}
        </Routes>
      </AuthContext.Provider>
  );
}

export default App;
