import React, { useState } from 'react';
import { MDBCol, MDBRow } from 'mdb-react-ui-kit';
import useUtils from '../../Hooks/utils';


function MinimizedCardComponent(article) {
  const {formatDate} = useUtils()

  const [Article,setArticle] = useState(article.article)

  console.log(article)
  return (
    <MDBRow className="mb-4 border-bottom pb-2">
    <MDBCol size="3">
      <img
        src={article.image}
        className="img-fluid shadow-1-strong rounded"
        alt="Hollywood Sign on The Hill"
      />
    </MDBCol>

    <MDBCol size="9">
      <p className="mb-2">
        <strong>{article.title}</strong>
      </p>
      <p>
        <u>{formatDate(article.published_at ?? '')}</u>
      </p>
    </MDBCol>
  </MDBRow>
  );
}

export default MinimizedCardComponent;
