import React from 'react';
import './auth.css';
import API_URL from '../config';
import { MDBBtn, MDBContainer, MDBRow, MDBCol, MDBCard, MDBCardBody, MDBInput, MDBCheckbox, MDBIcon } from 'mdb-react-ui-kit';
import {useForm} from "../Hooks/useForm";
import axios from "axios";

function SignUp() {
  {/*
  const handleSignUp = async () => {
    const name = document.getElementById('form1').value;
    const email = document.getElementById('form3').value;
    const password = document.getElementById('form4').value;
    const confirmPassword = document.getElementById('form5').value;

    axios.get(API_URL+'/sanctum/csrf-cookie', { withCredentials: false }).then((r) => {
    const userData = {
      name,
      email,
      password
    };

    try {
      const response = await fetch(API_URL+'/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(userData),
      });

      const data = await response.json();

      if (response.ok) {
        // Sign-up successful, redirect to sign-in page
        window.location.href = '/sign-in';
        // Show alert for email verification
        alert('Please verify your email.');
      } else {
        // Handle sign-up error
        console.log(data.error); // Log the specific error message returned from the API
      }
    } catch (error) {
      console.log(error); // Handle fetch or other general errors
    }
  });
  };

 */}

 const { setErrors, renderFieldError, navigate } = useForm();

  const handleSignUp = (e) => {
    e.preventDefault();

    const name = document.getElementById('form1').value;
    const email = document.getElementById('form3').value;
    const password = document.getElementById('form4').value;
    const password_confirmation = document.getElementById('form5').value;

    setErrors(null);
    axios.post(API_URL+'/api/register', {
        name,
        email,
        password,
        password_confirmation
    }).then(response => {
        console.log(response.data.user);
        if(response.data.user) {
            alert("Register success");
            navigate('/login');
        }
    }).catch(error => {
        console.log(error);
        if(error.response) {
            if (error.response.data.errors) {
                setErrors(error.response.data.errors);
            }
        }
    });
};

  return (
    <MDBContainer fluid className='p-4 background-radial-gradient overflow-hidden'>
      <MDBRow>
        <MDBCol md='6' className='text-center text-md-start d-flex flex-column justify-content-center'>
          <h1 className="my-5 display-3 fw-bold ls-tight px-3" style={{ color: 'hsl(218, 81%, 95%)' }}>
            The best offer <br />
            <span style={{ color: 'hsl(218, 81%, 75%)' }}>for your business</span>
          </h1>
          <p className='px-3' style={{ color: 'hsl(218, 81%, 85%)' }}>
            Lorem ipsum dolor sit amet consectetur adipisicing elit.
            Eveniet, itaque accusantium odio, soluta, corrupti aliquam
            quibusdam tempora at cupiditate quis eum maiores libero
            veritatis? Dicta facilis sint aliquid ipsum atque?
          </p>
        </MDBCol>
        <MDBCol md='6' className='position-relative'>
          <div id="radius-shape-1" className="position-absolute rounded-circle shadow-5-strong"></div>
          <div id="radius-shape-2" className="position-absolute shadow-5-strong"></div>
          <MDBCard className='my-5 bg-glass'>
            <MDBCardBody className='p-5'>
              <MDBInput wrapperClass='mb-4' label='UserName' id='form1' type='text' />
              <MDBInput wrapperClass='mb-4' label='Email' id='form3' type='email' />
              <MDBInput wrapperClass='mb-4' label='Password' id='form4' type='password' />
              <MDBInput wrapperClass='mb-4' label='confirmPassword' id='form5' type='password' />
              <MDBBtn className='w-100 mb-4' size='md' onClick={handleSignUp}>sign up</MDBBtn>
            </MDBCardBody>
          </MDBCard>
        </MDBCol>
      </MDBRow>
    </MDBContainer>
  );
}

export default SignUp;
