import React, { useEffect, useState } from 'react';
import { MDBFooter, MDBContainer } from 'mdb-react-ui-kit';
import { Slide } from 'react-slideshow-image';

import 'react-slideshow-image/dist/styles.css';
import apiClient from '../Services/api';

export default function Footer() {
  const [slideshowData, setSlideshowData] = useState([]);

  useEffect(() => {
    // Fetch slideshow data from the API
    fetchSlideshowData();
  }, []);

  const fetchSlideshowData = async () => {
    try {
      const response = await apiClient.get('/api/weekly');
      setSlideshowData(response.data);
    } catch (error) {
      console.error('Error fetching slideshow data:', error);
    }
  };

  return (
    <MDBFooter className="text-center text-white" style={{ backgroundColor: '#caced1' }}>
      <MDBContainer className="p-4">
        <div className="slideshow-container">
          {slideshowData.length > 0 && (
            <Slide
              autoplay
              duration={5000}
              transitionDuration={1000}
              infinite
              arrows={true}
              indicators={false}
              prevArrow={<div className="slide-arrow prev" />}
              nextArrow={<div className="slide-arrow next" />}
            >
              {slideshowData.map((item, index) => (
                <div key={index}>
                  <div className="slide-row">
                    {slideshowData.slice(index, index + 5).map((slide, subIndex) => (
                      <div key={subIndex}>
                        <img src={slide.image} alt="" />
                      </div>
                    ))}
                  </div>
                </div>
              ))}
            </Slide>
          )}
        </div>
      </MDBContainer>

      <div className="text-center p-3" style={{ backgroundColor: 'rgba(0, 0, 0, 0.2)' }}>
        Â© 2020 MDBootstrap.com
      </div>
    </MDBFooter>
  );
}
