import React, { useContext, useState } from 'react';
import InlineEdit from '../UI/InlineEditText';
import {
  MDBBtn,
  MDBModal,
  MDBModalDialog,
  MDBModalContent,
  MDBModalHeader,
  MDBModalTitle,
  MDBModalBody,
  MDBModalFooter,
  MDBCol,
  MDBRow,
  MDBCard,
  MDBCardText,
  MDBCardBody,
  MDBCardImage,
} from 'mdb-react-ui-kit';
import AuthContext from '../../Contexts/AuthContext';
import apiClient from '../../Services/api';
import { useForm } from '../../Hooks/useForm';

export default function ProfileModal({ showProfileModal, categories }) {
  const { authData } = useContext(AuthContext);

  const [basicModal, setBasicModal] = useState(true);
  const [userName, setUserName] = useState(authData && authData.user ? authData.user.name : '');
  const [image, setImage] = useState(authData && authData.user ? authData.user.image.trim().replace('=,','=') : '');
  const [selectedCategories, setSelectedCategories] = useState(categories.filter(c => authData && authData.user?.preferences?.category_id.includes(c.id)).map(el => el.id));

  const { setErrors, renderFieldError, message, setMessage, navigate } = useForm();

  const toggleShow = () => setBasicModal(!basicModal);
  const openFileInput = (e) => {
    e.stopPropagation();
    e.preventDefault()
    document.getElementById('file-input').click();
  }

  const handleFileSelect = (event) => {
    event.preventDefault();
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();

      reader.onload = handleFileRead;
      reader.readAsDataURL(file);
    }
  };

  const handleFileRead = (event) => {
    event.preventDefault();
    const base64Image = event.target.result;
    setImage(base64Image);
  };

  const handleCategoryClick = (categoryId) => {
    setSelectedCategories((prevSelectedCategories) => {
      const updatedCategories = [...prevSelectedCategories];
      const categoryIndex = updatedCategories.indexOf(categoryId);

      if (categoryIndex !== -1) {
        updatedCategories.splice(categoryIndex, 1); // Remove category if already selected
      } else {
        updatedCategories.push(categoryId); // Add category if not selected
      }

      return updatedCategories;
    });
  };

  const handleSaveChanges = (e) => {
    e.preventDefault();

    // Gather all the data from the modal
    const updatedData = {
      name: userName,
      preferences: {
        user_id: +authData.user.id,
        category_id: selectedCategories,
        source_id: [],
        author_id: [],
      },
      image: image,
    };

    apiClient
      .post('/api/updateProfile', updatedData)
      .then((response) => {
        console.log(response)
        if(response.status == 200 || response.status == 204) {
          setBasicModal(false);
          navigate('/')
        }
      })
      .catch((error) => {
        if (error.response.data.message) {
          setMessage(error.response.data.message);
        }
        if (error.response.data.errors) {
          setErrors(error.response.data.errors);
        }
      });
  };


  return (
    <>
      <MDBModal show={basicModal} setShow={setBasicModal} tabIndex='-1'>
        <MDBModalDialog>
          <MDBModalContent>
            <MDBModalHeader>
              <MDBModalTitle>Edit profile</MDBModalTitle>
              <MDBBtn className='btn-close' color='none' onClick={toggleShow}></MDBBtn>
            </MDBModalHeader>
            <MDBModalBody>
            <MDBCol>
                {
                    message && <div className="alert alert-danger">{message}</div>
                }
              </MDBCol>
              <div style={{ backgroundColor: '#9de2ff' }}>
                <MDBCard>
                  <div
                    className='text-white d-flex flex-row'
                    style={{ backgroundColor: '#000', height: '200px' }}
                  >
                    <div className='ms-4 mt-5 d-flex flex-column' style={{ width: '150px' }}>
                      {image && (
                        <MDBCardImage
                          src={image}
                          alt='Generic placeholder image'
                          className='mt-4 mb-2 img-thumbnail'
                          fluid
                          style={{ width: '150px', zIndex: '1' }}
                        />
                      )}
                      <MDBBtn outline color='dark' style={{ height: '36px', overflow: 'visible' }}  onClick={openFileInput}>
                        Change picture
                      </MDBBtn>
                      <input type='file' onClick={handleFileSelect} hidden id='file-input' />
                    </div>
                    <div className='ms-3' style={{ marginTop: '130px' }}>
                      <InlineEdit value={userName} setValue={setUserName} />
                    </div>
                  </div>
                  <MDBCardBody className='mt-5 text-black p-4'>
                    <div className='mb-5'>
                      <p className='lead fw-normal mb-1'>Preferred categories</p>
                      <div className='p-4' style={{ backgroundColor: '#f8f9fa' }}>
                        {/* Map over the categories and render the buttons */}
                        {categories.map((cat) => (
                          <MDBBtn
                            outline
                            rounded
                            key={cat.id}
                            className={`m-1 ${selectedCategories.includes(cat.id) ? 'bg-primary' : ''}`}
                            color='secondary'
                            onClick={(e) => {
                              e.preventDefault();
                              handleCategoryClick(cat.id);
                            }}
                          >
                            {cat.name}
                          </MDBBtn>
                        ))}
                      </div>
                    </div>

                    <div className='mb-5'>
                      <p className='lead fw-normal mb-1'>Preferred categories</p>
                      <div className='p-4' style={{ backgroundColor: '#f8f9fa' }}>
                        {/* Map over the categories and render the buttons */}
                        {categories.map((cat) => (
                          <MDBBtn
                            outline
                            rounded
                            key={cat.id}
                            className={`m-1 ${selectedCategories.includes(cat.id) ? 'bg-primary' : ''}`}
                            color='secondary'
                            onClick={(e) => {
                              e.preventDefault();
                              handleCategoryClick(cat.id);
                            }}
                          >
                            {cat.name}
                          </MDBBtn>
                        ))}
                      </div>
                    </div>

                    <div className='d-flex justify-content-between align-items-center mb-4'>
                      <MDBCardText className='lead fw-normal mb-0'>Recent Activities</MDBCardText>
                    </div>
                    <MDBRow>
                      <MDBCol className='mb-2'>{/* <MinimizedCardComponent /> */}</MDBCol>
                    </MDBRow>
                  </MDBCardBody>
                </MDBCard>
              </div>
            </MDBModalBody>

            <MDBModalFooter>
              <MDBBtn onClick={handleSaveChanges}>Save preferences</MDBBtn>
            </MDBModalFooter>
          </MDBModalContent>
        </MDBModalDialog>
      </MDBModal>
    </>
  );
}
