import { useEffect, useState } from 'react';

const useUtils = () => {
  const [formattedDate, setFormattedDate] = useState('');

  const formatDate = (date) => {
    return date.split(' ')[0].split('-').reverse().join('.'); // "2023-05-11 06:32:00" => "11.05.2023"
  };


  return {
    formattedDate,
    formatDate,
  };
};

export default useUtils;
