import React, { useEffect, useState } from 'react';
import { MDBFooter, MDBContainer } from 'mdb-react-ui-kit';
import { Carousel } from 'react-responsive-carousel';

import 'react-responsive-carousel/lib/styles/carousel.min.css';
import apiClient from '../Services/api';

export default function Footer() {
  const [slideshowData, setSlideshowData] = useState([]);

  useEffect(() => {
    // Fetch slideshow data from the API
    fetchSlideshowData();
  }, []);

  const fetchSlideshowData = async () => {
    try {
      const response = await apiClient.get('/api/weekly');
      setSlideshowData(response.data);
    } catch (error) {
      console.error('Error fetching slideshow data:', error);
    }
  };

  return (
    <MDBFooter className="text-center text-white" style={{ backgroundColor: '#caced1' }}>
      <MDBContainer className="p-4">
        <div className="slideshow-container">
          <Carousel
            showStatus={false}
            showThumbs={false}
            infiniteLoop
            autoPlay
            interval={5000}
            showArrows={true}
            showIndicators={false}
            showStatus={false}
            showThumbs={false}
            showArrows={true}
            renderThumbs={() => {}}
            renderArrowPrev={(onClickHandler, hasPrev) =>
              hasPrev && <button className="carousel-arrow-prev" onClick={onClickHandler} />
            }
            renderArrowNext={(onClickHandler, hasNext) =>
              hasNext && <button className="carousel-arrow-next" onClick={onClickHandler} />
            }
          >
            {slideshowData.length > 0 &&
              slideshowData.map((item, index) => (
                <div key={index}>
                  {index % 5 === 0 && (
                    <div className="slide-row">
                      {slideshowData.slice(index, index + 5).map((slide, subIndex) => (
                        <div key={subIndex}>
                          <img src={slide.image} alt="" />
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              ))}
          </Carousel>
        </div>
      </MDBContainer>

      <div className="text-center p-3" style={{ backgroundColor: 'rgba(0, 0, 0, 0.2)' }}>
        Â© 2020 MDBootstrap.com
      </div>
    </MDBFooter>
  );
}
