import React from 'react';
import { MDBCol, MDBRipple, MDBRow , MDBIcon} from 'mdb-react-ui-kit';
import MinimizedCardComponent from './MinimizedCard';
import useUtils from '../../Hooks/utils';
function Content({ content }) {
    return <div dangerouslySetInnerHTML={{ __html: content }} />;
  }

const FullCardComponent = ({ article }) => {

  const {formatDate} = useUtils()
  const {navigateByUrl} = useUtils()
    let defaultImg = 'data:image/jpeg;base64,';

    console.log(article)

    return (
    <MDBCol lg="4" md="6" className="mb-4 mb-lg-0" onClick={navigateByUrl(article?.link)}>
      <div>
        <MDBRipple
          className="bg-image hover-overlay shadow-1-strong ripple rounded-5 mb-4"
          rippleTag="div"
          rippleColor="light"
        >
        <img
          src={article.image ?? defaultImg}
          className="img-fluid"
        />
          <a href="#!">
            <div
              className="mask"
              style={{ backgroundColor: "rgba(251, 251, 251, 0.15)" }}
            ></div>
          </a>
        </MDBRipple>
        <MDBRow className="mb-3">
          <MDBCol col="6">
            <a href="" className="text-info">
              <MDBIcon fas icon="plane" className="me-1" />
              { article?.source.name}
            </a>
            {/* <p>{article.}</p> */}
          </MDBCol>
          <MDBCol col="6" className="text-end">
            <u>{formatDate(article.published_at)}</u>
          </MDBCol>
        </MDBRow>
        <a href="#!" className="text-dark">
          <h5>{article.title}</h5>
          <Content content={article.content}/>
        </a>
        <hr />
        <a href="#!" className="text-dark">
          {article.related_articles.map( related => (
              <MinimizedCardComponent key={related.id + '*' + related.author_id} article={related}/>
          ))}
        </a>
      </div>
    </MDBCol>
  );
};

export default FullCardComponent;
