import React, { useEffect, useState } from 'react';
import { MDBFooter, MDBContainer, MDBCarousel, MDBCarouselInner, MDBCarouselItem, MDBRipple } from 'mdb-react-ui-kit';
import apiClient from '../Services/api';

export default function Footer() {
  const [slideshowData, setSlideshowData] = useState([]);

  useEffect(() => {
    // Fetch slideshow data from the API
    fetchSlideshowData();
  }, []);

  const fetchSlideshowData = async () => {
    try {
      const response = await apiClient.get('/api/fetchRecentNews');
      setSlideshowData(response.data);
    } catch (error) {
      console.error('Error fetching slideshow data:', error);
    }
  };

  return (
    <MDBFooter className="text-center text-white" style={{ backgroundColor: '#caced1' }}>
      <MDBContainer className="p-4">
        <MDBCarousel showIndicators fade>
          <MDBCarouselInner>
            {slideshowData.length > 0 &&
              slideshowData.map((item, index) => (
                <MDBCarouselItem itemId={index + 1} key={index}>
                  <MDBRipple rippleColor="light" className="bg-image hover-overlay shadow-1-strong rounded">
                    <img src={item.image} className="w-100" alt="" />
                    <a href="#!">
                      <div className="mask" style={{ backgroundColor: 'rgba(251, 251, 251, 0.2)' }}></div>
                    </a>
                  </MDBRipple>
                </MDBCarouselItem>
              ))}
          </MDBCarouselInner>
        </MDBCarousel>
      </MDBContainer>

      <div className="text-center p-3" style={{ backgroundColor: 'rgba(0, 0, 0, 0.2)' }}>
        Â© 2020 MDBootstrap.com
      </div>
    </MDBFooter>
  );
}
