import { useContext } from 'react';
const { authData } = useContext(AuthContext);

const useUtils = () => {

  const formatDate = (date) => {
    return date.split(' ')[0].split('-').reverse().join('.'); // "2023-05-11 06:32:00" => "11.05.2023"
  };

  const navigateByUrl = (article_id,url) => {
    if(authData && authData.user){
      saveArticle(article_id)
    }else {
      window.location.href(url,'_blank');
    }
  };

  const saveArticle = (article_id) => {
    apiClient.post('/api/save_article', {article_id}).then(response => {
      if(response.data) {
        window.location.href(url,'_blank');
        }
    }).catch(error => {
        if(error.response) {
          // do nothing for now ..
      }
    });
  }

  return {
    formatDate,
    navigateByUrl
  };
};

export default useUtils;
