  const dispatch = useDispatch();
  const articles = useSelector((state) => state.articles);
  const categories = useSelector((state) => state.categories);
  const textSearch = useSelector((state) => state.textSearch);
  const total = useSelector((state) => state.total);

  const fetchData = useCallback(() => {
    axios
      .get('http://localhost:8000/api/news', { withCredentials: false })
      .then((article_response) => {
        dispatch(fetchArticles(article_response.data['results']));
        dispatch(fetchTotal(article_response.data['total']));
      })
      .catch((error) => {
        console.error('Error fetching articles:', error);
      });

    axios
      .get('http://localhost:8000/api/categories', { withCredentials: false })
      .then((categories_response) => {
        dispatch(fetchCategories(categories_response.data));
      })
      .catch((error) => {
        console.error('Error fetching categories:', error);
      });
  }, [dispatch]);

  const fetchNewsByCategory = useCallback((category) => {
    axios
      .get(`${API_URL}/api/news?category=${category}`, { withCredentials: false })
      .then((article_response) => {
        dispatch(fetchArticles(article_response.data['results']));
        dispatch(fetchTotal(article_response.data['total']));
      })
      .catch((error) => {
        console.error('Error fetching articles:', error);
      });
  }, [dispatch]);

  const searchByText = useCallback((textSearchResult) => {
    axios
      .get(`${API_URL}/api/news?textSearch=${textSearch}`, { withCredentials: false })
      .then((article_response) => {
        dispatch(fetchArticles(article_response.data['results']));
        dispatch(fetchTotal(article_response.data['total']));
      })
      .catch((error) => {
        console.error('Error fetching articles:', error);
      });
  }, [dispatch]);

  return (
    <AuthContext.Provider value={{authData, setAuthData }}>
      <Routes>
        <Route
          path="/"
          element={
            <>
              <Header
                categories={categories}
                fetchNewsByCategory={fetchNewsByCategory}
                searchByText={[setSearchByText, searchByText]}
              />
              <Home articles={articles} totalItems={total} />
              <Footer />
            </>
          }
        />
        <Route path="/signIn" element={<SignIn />} />
        <Route path="/signUp" element={<SignUp />} />
        {/* Add your other routes here */}
      </Routes>
    </AuthContext.Provider>
  );
}

export default App;
