import React, { useState } from 'react';
import { useSelector , useDispatch} from 'react-redux';
import InlineEdit from '../UI/InlineEditText';
import axios from 'axios';
import {
  updateProfilePicture,
  updateUsername,
  updatePreferences,
} from '../../Redux/user/userActions';

import {
  MDBBtn,
  MDBModal,
  MDBModalDialog,
  MDBModalContent,
  MDBModalHeader,
  MDBModalTitle,
  MDBModalBody,
  MDBModalFooter,
  MDBCol, MDBRow, MDBCard, MDBCardText, MDBCardBody, MDBCardImage } from 'mdb-react-ui-kit';
import MinimizedCardComponent from '../Cards/MinimizedCard';
import AuthContext from "../../Contexts/AuthContext";
import API_URL from '../../config';

export default function ProfileModal(showProfileModal) {
  const [basicModal, setBasicModal] = useState(true);
  const categories = useSelector(state => state.categories.map(c => {return {...c,isChecked:true}}));
  const [userName, setUserName] = React.useState("Safwan");
  const [image, setImage] = useState(AuthContext.user?.image)
  //const toggleShow = () => setBasicModal(!basicModal);

  console.log(categories)
  const dispatch = useDispatch();

  const handleProfilePictureUpdate = (pictureUrl) => {
    dispatch(updateProfilePicture(pictureUrl));
  };

  const handleUsernameUpdate = (username) => {
    dispatch(updateUsername(username));
  };

  const handlePreferencesUpdate = (preferences) => {
    dispatch(updatePreferences(preferences));
  };

  const openFileInput = (e) => {
    e.stopPropagation();
    document.getElementById('file-input').click();
  }

  const handleFileSelect = (event) => {
    const file = event.target.files[0];

    if (file) {
      const reader = new FileReader();

      reader.onload = handleFileRead;
      reader.readAsDataURL(file);
    }
  };

  const handleFileRead = (event) => {
    const base64Image = event.target.result;
    setImage(base64Image)
    console.log(base64Image);
    // Do something with the base64Image, such as updating state or sending it to the server
  };

  const handleSaveChanges = () => {
    // Gather all the data from the modal
    const updatedData = {
      userName: userName,
      preferences: categories.filter(cat => cat.isChecked),
      image: image
    };

    axios
    .post([API_URL]+'/api/editSettings', updatedData)
    .then(response => {
      // Handle the successful response
      console.log(response.data);
    })
    .catch(error => {
      // Handle the error
      console.error(error);
    });
  };

  return (
    <>
      <MDBModal show={basicModal} setShow={setBasicModal} tabIndex='-1'>
        <MDBModalDialog>
          <MDBModalContent>
            <MDBModalHeader>
              <MDBModalTitle>Edit profile</MDBModalTitle>
              {/*<MDBBtn className='btn-close' color='none' onClick={toggleShow}></MDBBtn>*/}
            </MDBModalHeader>
            <MDBModalBody>
                <div style={{ backgroundColor: '#9de2ff' }}>
                    <MDBCard>
                    <div className="text-white d-flex flex-row" style={{ backgroundColor: '#000', height: '200px' }}>
                        <div className="ms-4 mt-5 d-flex flex-column" style={{ width: '150px' }}>
                        <MDBCardImage src="https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-profiles/avatar-1.webp"
                            alt="Generic placeholder image" className="mt-4 mb-2 img-thumbnail" fluid style={{ width: '150px', zIndex: '1' }} />
                        <MDBBtn outline color="dark" style={{height: '36px', overflow: 'visible'}} onClick={openFileInput}>
                            Change picture
                        </MDBBtn>
                        <input type="file" onClick={handleFileSelect} hidden id='file-input'/>
                        </div>
                        <div className="ms-3" style={{ marginTop: '130px' }}>
                        <InlineEdit value={userName} setValue={setUserName} />
                        </div>
                    </div>
                    <MDBCardBody className="mt-5 text-black p-4">
                        <div className="mb-5">
                        <p className="lead fw-normal mb-1">Preferences</p>
                        <div className="p-4" style={{ backgroundColor: '#f8f9fa' }}>
                        {categories.map((cat) => {
                            return (
                                <MDBBtn outline rounded key={cat.id}  className={`m-1 ${[1,5].includes(cat.id) ? 'bg-primary' : ''}`} color='secondary'>
                                    {cat.name}
                                </MDBBtn>
                            )
                        })}
                        </div>
                        </div>
                        <div className="d-flex justify-content-between align-items-center mb-4">
                        <MDBCardText className="lead fw-normal mb-0">Recent Activities</MDBCardText>
                        </div>
                        <MDBRow>
                            <MDBCol className="mb-2">
                                <MinimizedCardComponent />
                            </MDBCol>
                        </MDBRow>
                    </MDBCardBody>
                    </MDBCard>
                </div>
            </MDBModalBody>

            <MDBModalFooter>
              {/*<MDBBtn color='secondary' onClick={toggleShow}>
                Close
                    </MDBBtn>*/}
              <MDBBtn onClick={handleSaveChanges}>Save preferences</MDBBtn>
            </MDBModalFooter>
          </MDBModalContent>
        </MDBModalDialog>
      </MDBModal>
    </>
  );
}
