import React, { useState } from 'react';
import {
  MDBContainer,
  MDBNavbar,
  MDBNavbarBrand,
  MDBNavbarToggler,
  MDBIcon,
  MDBNavbarNav,
  MDBNavbarItem,
  MDBNavbarLink,
  MDBBtn,
  MDBDropdown,
  MDBDropdownToggle,
  MDBDropdownMenu,
  MDBDropdownItem,
  MDBCollapse,
} from 'mdb-react-ui-kit';

export default function Header({ categories, fetchNewsByCategory, searchByText }) {
  const [showBasic, setShowBasic] = useState(false);

  const handleCategoryClick = (category) => {
    // Call the fetchNewsByCategory function with the selected category
    fetchNewsByCategory(category);
  };

  const handleSearch = (searchText) => {
    //searchByText.(searchText);
  }

  return (
    <MDBNavbar expand='lg' light bgColor='light'>
      <MDBContainer fluid>
        <MDBNavbarToggler
          aria-controls='navbarSupportedContent'
          aria-expanded='false'
          aria-label='Toggle navigation'
          onClick={() => setShowBasic(!showBasic)}
        >
          <MDBIcon icon='bars' fas />
        </MDBNavbarToggler>

        <MDBCollapse navbar show={showBasic}>
          <MDBNavbarNav className='mr-auto mb-2 mb-lg-0'>
            {categories.map((category) => (
              <MDBNavbarItem key={category.id} className='cursor-pointer'>
                <MDBNavbarLink
                  active
                  aria-current='page'
                  onClick={() => handleCategoryClick(category.id)}
                >
                  {category.name.toUpperCase().slice(0,1) + category.name.toLowerCase().slice(1,category.name.length) }
                </MDBNavbarLink>
              </MDBNavbarItem>
            ))}
          </MDBNavbarNav>

          <form className='d-flex input-group w-auto'>
            <input
              type='search'
              className='form-control'
              placeholder='Search by category or a name ...'
              aria-label='Search'
              onChange={(e) => handleSearch(e.target.value)}
            />
            { !authenticated ? (
              <MDBBtn style={{"margin-left": '0.5rem'}} color='primary'>Sign In</MDBBtn> 
            ) : <MDBContainer fluid>
            <MDBNavbarNav className='d-flex flex-row'>
              <MDBNavbarItem className='me-3 me-lg-0'>
                <MDBNavbarLink href='#'>
                  <MDBIcon fas icon='shopping-cart' />
                </MDBNavbarLink>
              </MDBNavbarItem>
              <MDBNavbarItem className='me-3 me-lg-0'>
                <MDBNavbarLink href='#'>
                  <MDBIcon fab icon='twitter' />
                </MDBNavbarLink>
              </MDBNavbarItem>
            </MDBNavbarNav>
          </MDBContainer>}
          </form>
        </MDBCollapse>
      </MDBContainer>
    </MDBNavbar>
  );
}
