import React, { useCallback, useEffect, useState } from "react";
import { BrowserRouter, Route, Routes } from "react-router-dom";
import axios from "axios";
import debounce from "lodash/debounce";

import Header from "../Layouts/Header";
import Footer from "../Layouts/Footer";
import Home from "./Home";

import SignIn from "..Auth/SignIn";
import SignUp from "..Auth/SignUp";

function App() {
  const [articles, setArticles] = useState([]);
  const [categories, setCategories] = useState([]);
  const [textSearch, setSearchByText] = useState([])

  const fetchData = useCallback(() => {
    axios.get("http://localhost:8000/api/news", { withCredentials: false })
      .then((article_response) => {
        setArticles(article_response.data);
      })
      .catch((error) => {
        console.error("Error fetching articles:", error);
      });

    axios.get("http://localhost:8000/api/categories", { withCredentials: false })
      .then((categories_response) => {
        setCategories(categories_response.data);
      })
      .catch((error) => {
        console.error("Error fetching categories:", error);
      });
  }, []);

  const fetchNewsByCategory = useCallback((category) => {
    axios.get(`http://localhost:8000/api/news?category=${category}` , { withCredentials: false })
    .then((article_response) => {
      setArticles(article_response.data);
      console.log(articles)
    })
    .catch((error) => {
      console.error("Error fetching articles:", error);
    });
  })


  const searchByText = useCallback ((textSearchResult) => {
    axios.get(`http://localhost:8000/api/news?textSearch=${textSearch}` , { withCredentials: false })
    .then((article_response) => {
      setArticles(article_response.data);
      console.log(articles)
    })
    .catch((error) => {
      console.error("Error fetching articles:", error);
    });
  })
  useEffect(() => {
    fetchData();

    const interval = setInterval(fetchData, 10 * 60 * 1000);

    return () => clearInterval(interval);
  }, [fetchData]);

  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={
            <>
              <Header categories={categories} fetchNewsByCategory={fetchNewsByCategory} searchByText={[setSearchByText,searchByText]} />
              <RouteIndex articles={articles} />
              <Footer />
            </>
          }
        />

        <Route path="/signIn" element={
            <>
              <SignIn />
            </>
          }
        />

        <Route  path="/signUp" element={
            <>
              <SignUp />
            </>
          }
        />
      </Routes>
    </BrowserRouter>
  );
}

function RouteIndex({ articles }) {
  return <Route element={<Home articles={articles} />} />;
}

export default App;
