import {useCallback, useEffect,useState, React} from "react";
import { BrowserRouter, Route, Routes } from "react-router-dom";
import debounce from "lodash/debounce";
import axios from "axios";

import Header from "../Layouts/Header";
import Footer from "../Layouts/Footer";
import Home from "./Home";



function App() {

  const [articles, setArticles] = useState([])
  const [categories, setCategories] = useState([])



  // fetch data from the API each 10 mins
  const fetchData = async () => {
    try {
      const article_response = await axios.get(`http://localhost:8000/api/news`, {headers: {withCredentials: false}});
      setArticles(...article_response.data);

      const categories_response = await axios.get(`http://localhost:8000/api/categories`, {headers: {withCredentials: false}});
      setCategories(...categories_response.data);
      console.log(categories)

    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  useEffect(() => {
    // Fetch data initially
    fetchData();

    // Fetch data every 10 minutes
    const interval = setInterval(fetchData, 10 * 60 * 1000);

    // Clean up the interval when the component is unmounted
    return () => clearInterval(interval);
  }, []);



  return (
    <BrowserRouter>
      <Header {...categories}/>

      <Routes>
        <Route path="/" element={<Home  {...articles}/>} />
      </Routes>

      <Footer />
    </BrowserRouter>
  );
}

export default App;
