import {React, useState, useEffect} from "react";

import {
  MDBBtn,
  MDBCard,
  MDBContainer,
  MDBCol,
  MDBIcon,
  MDBPagination,
  MDBPaginationItem,
  MDBPaginationLink,
  MDBRipple,
  MDBRow,
} from "mdb-react-ui-kit";

import FullCardComponent from './Cards/FullCard';
import Pagination from './Pagination';

export default function Home({articles,totalItems}) {
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [itemsPerPage] = useState(3);
  const [paginatedItems, setPaginatedItems] = useState([]);

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  //if(articles && articles.length > 0) {
      useEffect(()=>{
      setPaginatedItems(articles?.slice(indexOfFirstItem, indexOfLastItem))
      setTotalPages(Math.floor(totalItems / itemsPerPage));
      console.log(paginatedItems)
    }, [])
  //}

  const handlePageChange = (page) => {
    console.log(page)
    setCurrentPage(page);
  };
  //if(articles && articles.length > 0) {
    useEffect(() => {
      setPaginatedItems(articles?.slice(indexOfFirstItem, indexOfLastItem));
    }, [articles, indexOfFirstItem, indexOfLastItem]);
  //}

  return (
    <MDBContainer className="py-5">
      <MDBRow className="gx-5 border-bottom pb-4 mb-5">
        <MDBCol md="6" className="mb-4">
          <MDBRipple
            className="bg-image hover-overlay ripple shadow-2-strong rounded-5"
            rippleTag="div"
            rippleColor="light"
          >
            <img
              src="https://mdbcdn.b-cdn.net/img/new/slides/080.webp"
              className="w-100"
            />
            <a href="#!">
              <div
                className="mask"
                style={{ backgroundColor: "rgba(251, 251, 251, 0.15)" }}
              ></div>
            </a>
          </MDBRipple>
        </MDBCol>
        <MDBCol md="6" className="mb-4">
          <span className="badge bg-danger px-2 py-1 shadow-1-strong mb-3">
            News of the day
          </span>
          <h4>
            <strong>Facilis consequatur eligendi</strong>
          </h4>
          <p className="text-muted">
            Lorem ipsum dolor sit amet consectetur adipisicing elit. Facilis
            consequatur eligendi quisquam doloremque vero ex debitis veritatis
            placeat unde animi laborum sapiente illo possimus, commodi
            dignissimos obcaecati illum maiores corporis.
          </p>
          <MDBBtn>Read More</MDBBtn>
        </MDBCol>
      </MDBRow>
      <MDBRow className="gx-lg-5">
        {articles && paginatedItems.map((article) => (
          <FullCardComponent article={article}/>
        ))}
      </MDBRow>
      <Pagination currentPage={currentPage} totalPages={totalPages} onPageChange={handlePageChange} />
    </MDBContainer>
  );
}
