import React, { useContext, useEffect, useState } from 'react';

import AuthContext from '../Contexts/AuthContext';
import ProfileModal from '../Components/Modals/ProfileModal';
import { useAuth } from '../Hooks/useAuth';
import {
  MDBContainer,
  MDBNavbar,
  MDBNavbarBrand,
  MDBNavbarToggler,
  MDBIcon,
  MDBNavbarNav,
  MDBNavbarItem,
  MDBNavbarLink,
  MDBBtn,
  MDBCollapse,
  MDBBadge
} from 'mdb-react-ui-kit';

export default function Header({ props, fetchNewsByCategory, searchByText }) {
  const [showBasic, setShowBasic] = useState(false);
  const { authData } = useContext(AuthContext);
  const [showProfile, setShowProfile] = useState(false);
  const [image, setImage] = useState(null);
  const [preferences, setPreferences] = useState(null);
  const { setLogout } = useAuth();

  useEffect(() => {
    setImage(authData.user?.image?.trim().replace('=,', '='));
    setPreferences(authData.user?.preferences);
  }, [authData]);

  const handleCategoryClick = (category) => {
    // Call the fetchNewsByCategory function with the selected category
    fetchNewsByCategory(category);
  };

  const handleSearch = (searchText) => {
    searchByText(searchText);
  };

  const handleShowProfile = () => {
    setShowProfile(true);
  };

  const handleLogout = () => {
    setLogout();
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-light bg-light">
      <div className="container-fluid">
        <button
          className="navbar-toggler"
          type="button"
          data-toggle="collapse"
          data-target="#navbarSupportedContent"
          aria-controls="navbarSupportedContent"
          aria-expanded="false"
          aria-label="Toggle navigation"
          onClick={() => setShowBasic(!showBasic)}
        >
          <i className="fas fa-bars"></i>
        </button>

        <div className={`collapse navbar-collapse${showBasic ? ' show' : ''}`} id="navbarSupportedContent">
          {/* <a className="navbar-brand mt-2 mt-lg-0" href="#">
            <img
              src="https://mdbcdn.b-cdn.net/img/logo/mdb-transaprent-noshadows.webp"
              height="15"
              alt="MDB Logo"
              loading="lazy"
            />
          </a> */}

          <ul className="navbar-nav me-auto mb-2 mb-lg-0">
            {props.categories
              .filter(
                (cat) =>
                  !preferences ||
                  (preferences && preferences.category_id.includes(cat.id))
              )
              .map((category) => (
                <li className="nav-item" key={category.id}>
                  <a className="nav-link" href="#" onClick={() => handleCategoryClick(category.id)}>
                    {category.name}
                  </a>
                </li>
              ))}
          </ul>

          <form className="d-flex input-group w-full">
            <input
              type="search"
              className="form-control"
              placeholder="Search by a keyword? (Exp: Apple 23/05/2023)"
              aria-label="Search"
              onChange={(e) => handleSearch(e.target.value)}
            />

            {authData.signedIn && authData.user ? (
              <div className="d-flex align-items-center">
                <div className="text-reset me-3" onClick={handleShowProfile}>
                  <i className="fas fa-shopping-cart"></i>
                </div>

                <div className="dropdown">
                  <a
                    className="text-reset me-3 dropdown-toggle hidden-arrow"
                    href="#"
                    id="navbarDropdownMenuLink"
                    role="button"
                    data-toggle="dropdown"
                    aria-expanded="false"
                  >
                    <i className="fas fa-bell"></i>
                    <span className="badge rounded-pill badge-notification bg-danger">1</span>
                  </a>
                  <ul
                    className="dropdown-menu dropdown-menu-end"
                    aria-labelledby="navbarDropdownMenuLink"
                  >
                    <li>
                      <a className="dropdown-item" href="#">Some news</a>
                    </li>
                    <li>
                      <a className="dropdown-item" href="#">Another news</a>
                    </li>
                    <li>
                      <a className="dropdown-item" href="#">Something else here</a>
                    </li>
                  </ul>
                </div>

                <div className="dropdown">
                  <a
                    className="dropdown-toggle d-flex align-items-center hidden-arrow"
                    href="#"
                    id="navbarDropdownMenuAvatar"
                    role="button"
                    data-toggle="dropdown"
                    aria-expanded="false"
                  >
                    <img
                      src="https://mdbcdn.b-cdn.net/img/new/avatars/2.webp"
                      className="rounded-circle"
                      height="25"
                      alt="Black and White Portrait of a Man"
                      loading="lazy"
                    />
                  </a>
                  <ul
                    className="dropdown-menu dropdown-menu-end"
                    aria-labelledby="navbarDropdownMenuAvatar"
                  >
                    <li>
                      <a className="dropdown-item" href="#">My profile</a>
                    </li>
                    <li>
                      <a className="dropdown-item" href="#">Settings</a>
                    </li>
                    <li>
                      <a className="dropdown-item" href="#">Logout</a>
                    </li>
                  </ul>
                </div>
              </div>
            ) : (
              <MDBBtn
                style={{ marginLeft: '0.5rem' }}
                className="btn-sm"
                color="tertiary"
                href="/signIn"
              >
                Sign In
              </MDBBtn>
            )}
          </form>
        </div>
      </div>
    </nav>
  );
}
