import {useCallback, useEffect,useState, React} from "react";
import debounce from "lodash/debounce";
import { BrowserRouter, Route, Routes } from "react-router-dom";

import Header from "../Layouts/Header";
import Footer from "../Layouts/Footer";
import Home from "./Home";

import axios from "axios";


function App() {

  const [articles, setArticles] = useState([]);
const [categories, setCategories] = useState([]);

const fetchData = useCallback(() => {
    axios.get(`http://localhost:8000/api/news`, { headers: { withCredentials: false } }).then((article_response)=> {
      setArticles(article_response.data);
      console.log(articles);
    });

    axios.get(`http://localhost:8000/api/categories`, { headers: { withCredentials: false } }).then(
      categories_response => {
        setCategories(categories_response.data);
        console.log(categories)
      }
    )
});

useEffect(() => {
  setArticles(articles);
}, [articles]);

useEffect(() => {
  setCategories(categories);
}, [categories]);


useEffect(() => {
  // Fetch data initially
  fetchData();

  // Fetch data every 10 minutes
  const interval = setInterval(fetchData, 1 * 60 * 1000);

  // Clean up the interval when the component is unmounted
  return () => clearInterval(interval);
}, []);

  return (
    <BrowserRouter>

      <Header {...categories} />
      <Routes>
        <Route path="/" element={<Home  {...articles}/>} />
      </Routes>

      <Footer />
    </BrowserRouter>
  );
}

export default App;
