import React from 'react';
import { MDBCol, MDBRipple, MDBRow , MDBIcon} from 'mdb-react-ui-kit';
import MinimizedCardComponent from './MinimizedCard';

function Content({ content }) {
    return <div dangerouslySetInnerHTML={{ __html: content }} />;
  }

const FullCardComponent = ({ article }) => {
    console.log(article)

    const formatDate = (date) => {
        return date.split(' ')[0].split('-').reverse().join('.') // "2023-05-11 06:32:00" => "11.05.2023"
    }
  return (
    <MDBCol lg="4" md="6" className="mb-4 mb-lg-0">
      <div>
        <MDBRipple
          className="bg-image hover-overlay shadow-1-strong ripple rounded-5 mb-4"
          rippleTag="div"
          rippleColor="light"
        >
        <img
          src={article.image ?? 'data:image/jpeg;base64,'}
          className="img-fluid"
        />
          <a href="#!">
            <div
              className="mask"
              style={{ backgroundColor: "rgba(251, 251, 251, 0.15)" }}
            ></div>
          </a>
        </MDBRipple>
        <MDBRow className="mb-3">
          <MDBCol col="6">
            <a href="" className="text-info">
              <MDBIcon fas icon="plane" className="me-1" />
              { article?.source.name}
            </a>
          </MDBCol>
          <MDBCol col="6" className="text-end">
            <u>{formatDate(article.published_at)}</u>
          </MDBCol>
        </MDBRow>
        <a href="#!" className="text-dark">
          <h5>{article.title}</h5>
          <Content content={article.content}/>
        </a>
        <hr />
        <a href="#!" className="text-dark">
        <MinimizedCardComponent />
        </a>
      </div>
    </MDBCol>
  );
};

export default FullCardComponent;
