import React, { useState, useRef, useEffect } from "react";
import "./filter.css";
import FilterModal from "./FilterModal";

export default function MiniFilter({ children, onApply, label }) {
  const [isOpen, setIsOpen] = useState(false);
  const dropdownRef = useRef(null);
  const buttonRef = useRef(null);
  const modalRef = useRef(null);

  useEffect(() => {
    const handleClickOutside = (event) => {
      const isDropdownClick = dropdownRef.current && dropdownRef.current.contains(event.target);
      const isButtonClick = buttonRef.current && buttonRef.current.contains(event.target);
      const isModalClick = modalRef.current && modalRef.current.contains(event.target);

      if (isDropdownClick || isButtonClick || isModalClick) {
        // If the user clicked on the menu, we don't do anything
        return;
      }

      // Otherwise, we close the menu
      setIsOpen(false);
    };

    document.addEventListener("mousedown", handleClickOutside); // handle desktops
    document.addEventListener("touchstart", handleClickOutside); // handle touch devices

    // Event cleanup
    return () => {
      document.removeEventListener("mousedown", handleClickOutside); // handle desktops
      document.removeEventListener("touchstart", handleClickOutside); // handle touch devices
    };
  }, []);

  const handleApply = () => {
    setIsOpen(false);
    onApply();
  };

  return (
    <div className="filter">
      <button ref={buttonRef} onClick={() => setIsOpen(!isOpen)} className="filter__button">
        {label}
      </button>

      {isOpen && (
        <>
          <div ref={dropdownRef} className="filter__dropdown">
            <div style={{ paddingTop: "2rem", paddingBottom: "2rem" }}>{children}</div>
          </div>

          <div className="filter__dropdown__actions">
            <button onClick={handleApply} className="filter__dropdown_button">
              Apply
            </button>
          </div>
        </>
      )}

      {isOpen && (
        <FilterModal ref={modalRef} onApply={handleApply} onDismiss={() => setIsOpen(false)}>
          {children}
        </FilterModal>
      )}
    </div>
  );
}
