import React, { useState } from "react";
import "./filter.css";
import MiniFilter from "./MiniFilter";

const technologies = ["React", "Python", "PHP", "Node.js", "Ember", "Vue"];

export default function Filters() {
  const [selectedTechnologies, setSelectedTechnologies] = useState([]);

  const handleSelect = (technology) => {
    const isSelected = selectedTechnologies.includes(technology);
    const newSelection = isSelected
      ? selectedTechnologies.filter((currentTech) => currentTech !== technology)
      : [...selectedTechnologies, technology];
    setSelectedTechnologies(newSelection);
  };

  return (
    <div className="App">
      <h1>Building responsive filter components on React</h1>
      <MiniFilter label="Technologies" onApply={() => alert(selectedTechnologies)} />
      <div className="technologies-list">
        {technologies.map((tech, index) => {
          const isSelected = selectedTechnologies.includes(tech);
          return (
            <label key={index}>
              <input
                type="checkbox"
                checked={isSelected}
                onChange={() => handleSelect(tech)}
              />
              <span className="ml-2 text-base text-gray-500 font-heading">{tech}</span>
            </label>
          );
        })}
      </div>
    </div>
  );
}
