import React from 'react';
import { MDBPagination, MDBPaginationItem, MDBPaginationLink } from 'mdb-react-ui-kit';

const Pagination = ({ currentPage, totalPages }) => {
  const renderPaginationItems = () => {
    const items = [];

    // Previous Page
    items.push(
      <MDBPaginationItem key="previous" disabled={currentPage === 1}>
        <MDBPaginationLink href="#" tabIndex={-1} aria-disabled="true">
          Previous
        </MDBPaginationLink>
      </MDBPaginationItem>
    );

    // Page Numbers
    for (let page = 1; page <= totalPages; page++) {
      items.push(
        <MDBPaginationItem key={page} active={currentPage === page}>
          <MDBPaginationLink href="#">{page}</MDBPaginationLink>
        </MDBPaginationItem>
      );
    }

    // Next Page
    items.push(
      <MDBPaginationItem key="next" disabled={currentPage === totalPages}>
        <MDBPaginationLink href="#">Next</MDBPaginationLink>
      </MDBPaginationItem>
    );

    return items;
  };

  return (
    <nav aria-label="...">
      <MDBPagination circle className="mb-0 justify-content-center">
        {renderPaginationItems()}
      </MDBPagination>
    </nav>
  );
};

export default Pagination;
