import React, { useContext,useState } from 'react';
import {
  MDBContainer,
  MDBNavbar,
  MDBNavbarBrand,
  MDBNavbarToggler,
  MDBIcon,
  MDBNavbarNav,
  MDBNavbarItem,
  MDBNavbarLink,
  MDBBtn,
  MDBCollapse,
  MDBBadge
} from 'mdb-react-ui-kit';

import AuthContext from "../Contexts/AuthContext";
import ProfileModal from '../Components/Modals/ProfileModal'

export default function Header({ categories, fetchNewsByCategory, searchByText }) {
  const [showBasic, setShowBasic] = useState(false);  
  const {authData} = useContext(AuthContext);
  const [showProfile, setShowProfile] = useState(false);


  const handleCategoryClick = (category) => {
    // Call the fetchNewsByCategory function with the selected category
    fetchNewsByCategory(category);
  };

  const handleSearch = (searchText) => {
    searchByText(searchText);
  }

  const handleShowProfile = () => {
    setShowProfile(true);
  };

  return (
    <MDBNavbar expand='lg' light bgColor='light'>
      <MDBContainer fluid>
        <MDBNavbarToggler
          aria-controls='navbarSupportedContent'
          aria-expanded='false'
          aria-label='Toggle navigation'
          onClick={() => setShowBasic(!showBasic)}
        >
          <MDBIcon icon='bars' fas />
        </MDBNavbarToggler>

        <MDBCollapse navbar show={showBasic}>
          <MDBNavbarNav className='mr-auto mb-2 mb-lg-0'>
            {categories.map((category) => (
              <MDBNavbarItem key={category.id} className='cursor-pointer'>
                <MDBNavbarLink
                  active
                  aria-current='page'
                  onClick={() => handleCategoryClick(category.id)}
                >
                  {category.name.toUpperCase().slice(0,1) + category.name.toLowerCase().slice(1,category.name.length) }
                </MDBNavbarLink>
              </MDBNavbarItem>
            ))}
          </MDBNavbarNav>

          <form className='d-flex input-group w-full'>
            <input
              type='search'
              className='form-control'
              placeholder='Search by category or a name ...'
              aria-label='Search'
              onChange={(e) => handleSearch(e.target.value)}
            />
            { authData.signedIn && authData.user ? (
                <div className='d-flex'>
                  <div className='d-inline-flex position-relative'  onClick={handleShowProfile}>
                    <MDBBadge className='position-absolute top-0 start-100 translate-middle p-1 bg-danger border border-light rounded-circle'>
                      <span className='visually-hidden'>New alerts</span>
                    </MDBBadge>
                    <img
                      className='rounded-4 shadow-4'
                      src='https://mdbootstrap.com/img/Photos/Avatars/man2.jpg'
                      alt='Avatar'
                      style={{ width: '35px', height: '35px', marginLeft : '0.5rem' }}
                    />
                  </div>
                </div>
              ) : <MDBBtn style={{marginLeft: '0.5rem'}} color='primary' href='/signIn'>Sign In</MDBBtn> 
            }
            {showProfile && <ProfileModal showProfileModal={showProfile} />}
          </form>
        </MDBCollapse>
      </MDBContainer>
    </MDBNavbar>
  );
}
