import React, { useContext,useState } from 'react';
import { useSelector , useDispatch} from 'react-redux';
import InlineEdit from '../UI/InlineEditText';
import {
  updateProfilePicture,
  updateUsername,
  updatePreferences,
} from '../../Redux/user/userActions';

import {
  MDBBtn,
  MDBModal,
  MDBModalDialog,
  MDBModalContent,
  MDBModalHeader,
  MDBModalTitle,
  MDBModalBody,
  MDBModalFooter,
  MDBCol, MDBRow, MDBCard, MDBCardText, MDBCardBody, MDBCardImage } from 'mdb-react-ui-kit';
import MinimizedCardComponent from '../Cards/MinimizedCard';
import AuthContext from "../../Contexts/AuthContext";
import apiClient from '../../Services/api';
export default function ProfileModal(showProfileModal) {
  const {authData} = useContext(AuthContext);

  const [basicModal, setBasicModal] = useState(true);
  const categories = useSelector(state => state.categories.map(c => {
    const userPreferences = authData.user.preferences || {}; // Make sure preferences exist
    const isChecked = userPreferences.category_id && userPreferences.category_id.includes(c.id);
    return {...c, isChecked};
  }), [authData.user.preferences]);  
  const [userName, setUserName] = useState(authData && authData.user ? authData.user.name : '');
  const [image, setImage] = useState(authData && authData.user ? authData.user.image : '')

  //const toggleShow = () => setBasicModal(!basicModal);

  const dispatch = useDispatch();

  const handleProfilePictureUpdate = (pictureUrl) => {
    dispatch(updateProfilePicture(pictureUrl));
  };

  const handleUsernameUpdate = (username) => {
    dispatch(updateUsername(username));
  };


  const openFileInput = (e) => {
    e.stopPropagation();
    document.getElementById('file-input').click();
  }

  const handleFileSelect = (event) => {
    const file = event.target.files[0];

    if (file) {
      const reader = new FileReader();

      reader.onload = handleFileRead;
      reader.readAsDataURL(file);
    }
  };

  const handleFileRead = (event) => {
    const base64Image = event.target.result;
    setImage(base64Image)
    // Do something with the base64Image, such as updating state or sending it to the server
  };

  const handleCategoryClick = (categoryId) => {

     const updatedCategories = categories.map((category) => {
    if (category.id === categoryId) {
      return {
        ...category,
        isChecked: !category.isChecked,
      };
    }
    return category;
  }).filter(el => el.isChecked).map((category) => {return category.id});

  dispatch(updatePreferences({...authData.user.preferences ,category_id:updatedCategories}));
  };

  const handleSaveChanges = (e) => {
    e.preventDefault();

    // Gather all the data from the modal
    const updatedData = {
      name: userName,
      preferences: {
        user_id : +authData.user.id,
        category_id : categories.filter(cat => cat.isChecked).map((el) => {return el.id}),
        source_id : [],
        author_id : []
      },
      image: image
    };

    apiClient
    .post('/api/updateProfile', updatedData)
    .then(response => {
      setBasicModal(true)
    })
    .catch(error => {
      // Handle the error
      console.error(error);
    });
  };



  return (
    <>
      <MDBModal show={basicModal} setShow={setBasicModal} tabIndex='-1'>
        <MDBModalDialog>
          <MDBModalContent>
            <MDBModalHeader>
              <MDBModalTitle>Edit profile</MDBModalTitle>
              {/*<MDBBtn className='btn-close' color='none' onClick={toggleShow}></MDBBtn>*/}
            </MDBModalHeader>
            <MDBModalBody>
                <div style={{ backgroundColor: '#9de2ff' }}>
                    <MDBCard>
                    <div className="text-white d-flex flex-row" style={{ backgroundColor: '#000', height: '200px' }}>
                        <div className="ms-4 mt-5 d-flex flex-column" style={{ width: '150px' }}>
                        <MDBCardImage src={image}
                            alt="Generic placeholder image" className="mt-4 mb-2 img-thumbnail" fluid style={{ width: '150px', zIndex: '1' }} />
                        <MDBBtn outline color="dark" style={{height: '36px', overflow: 'visible'}} onClick={openFileInput}>
                            Change picture
                        </MDBBtn>
                        <input type="file" onClick={handleFileSelect} hidden id='file-input'/>
                        </div>
                        <div className="ms-3" style={{ marginTop: '130px' }}>
                        <InlineEdit value={userName} setValue={setUserName} />
                        </div>
                    </div>
                    <MDBCardBody className="mt-5 text-black p-4">
                        <div className="mb-5">
                        <p className="lead fw-normal mb-1">Preferences</p>
                        <div className="p-4" style={{ backgroundColor: '#f8f9fa' }}>
                        {categories.map((cat) => (
                          <MDBBtn
                            outline
                            rounded
                            key={cat.id}
                            className={`m-1 ${cat.isChecked ? 'bg-primary' : ''}`}
                            color='secondary'
                            onClick={(e) => {
                              e.preventDefault();
                              handleCategoryClick(cat.id)
                            }} // Call handleButtonClick with the category ID
                          >
                            {cat.name}
                          </MDBBtn>
                        ))}
                        </div>
                        </div>
                        <div className="d-flex justify-content-between align-items-center mb-4">
                        <MDBCardText className="lead fw-normal mb-0">Recent Activities</MDBCardText>
                        </div>
                        <MDBRow>
                            <MDBCol className="mb-2">
                                {/* <MinimizedCardComponent /> */}
                            </MDBCol>
                        </MDBRow>
                    </MDBCardBody>
                    </MDBCard>
                </div>
            </MDBModalBody>

            <MDBModalFooter>
              {/*<MDBBtn color='secondary' onClick={toggleShow}>
                Close
                    </MDBBtn>*/}
              <MDBBtn onClick={handleSaveChanges}>Save preferences</MDBBtn>
            </MDBModalFooter>
          </MDBModalContent>
        </MDBModalDialog>
      </MDBModal>
    </>
  );
}
