import React, { useCallback, useEffect, useState } from "react";
import { BrowserRouter, Route, Routes } from "react-router-dom";
import axios from "axios";

import API_URL from '../config';
import {useAuth} from "../Hooks/useAuth";
import AuthContext from "../Contexts/AuthContext";


import Home from "./Home";
import SignIn from "../Auth/SignIn";
import SignUp from "../Auth/SignUp";
import Header from "../Layouts/Header";
import Footer from "../Layouts/Footer";

function App() {
  window.axios.defaults.withCredentials = true;

  const [articles, setArticles] = useState([]);
  const [categories, setCategories] = useState([]);
  const [textSearch, setSearchByText] = useState([])

  const {userData} = useAuth();
  const [authData, setAuthData] = useState({signedIn: userData.signedIn, user: userData.user});
  const fetchData = useCallback(() => {
    axios.get("http://localhost:8000/api/news", { withCredentials: false })
      .then((article_response) => {
        setArticles(article_response.data);
      })
      .catch((error) => {
        console.error("Error fetching articles:", error);
      });

    axios.get("http://localhost:8000/api/categories", { withCredentials: false })
      .then((categories_response) => {
        setCategories(categories_response.data);
      })
      .catch((error) => {
        console.error("Error fetching categories:", error);
      });
  }, []);

  const fetchNewsByCategory = useCallback((category) => {
    axios.get(`${API_URL}/api/news?category=${category}` , { withCredentials: false })
    .then((article_response) => {
      setArticles(article_response.data);
      console.log(articles)
    })
    .catch((error) => {
      console.error("Error fetching articles:", error);
    });
  })


  const searchByText = useCallback ((textSearchResult) => {
    axios.get(`${API_URL}/api/news?textSearch=${textSearch}` , { withCredentials: false })
    .then((article_response) => {
      setArticles(article_response.data);
      console.log(articles)
    })
    .catch((error) => {
      console.error("Error fetching articles:", error);
    });
  })
  useEffect(() => {
    fetchData();

    const interval = setInterval(fetchData, 10 * 60 * 1000);

    return () => clearInterval(interval);
  }, [fetchData]);

  return (
    <AuthContext.Provider value={{authData, setAuthData }}>
      <Routes>
        <Route
          path="/"
          element={
            <>
              <Header
                categories={categories}
                fetchNewsByCategory={fetchNewsByCategory}
                searchByText={[setSearchByText, searchByText]}
              />
              <Home articles={articles} />
              <Footer />
            </>
          }
        />
        <Route path="/signIn" element={<SignIn />} />
        <Route path="/signUp" element={<SignUp />} />
        {/* Add your other routes here */}
      </Routes>
    </AuthContext.Provider>
  );
}

export default App;
