import React, { useContext,useEffect,useState } from 'react';
import AuthContext from "../Contexts/AuthContext";
import ProfileModal from '../Components/Modals/ProfileModal'
import { useAuth } from "../Hooks/useAuth";

function Navbar(props, fetchNewsByCategory, searchByText) {

  const [showBasic, setShowBasic] = useState(false);  
  const {authData} = useContext(AuthContext);
  const [showProfile, setShowProfile] = useState(false);
  const [image, setImage] = useState(null);
  const [preferences, setPreferences] = useState(null);
  const {setLogout} = useAuth()

  useEffect(() => {
    setImage(authData.user?.image?.trim().replace('=,','='))
    setPreferences(authData.user?.preferences)
  },[authData])

  const handleCategoryClick = (category) => {
    // Call the fetchNewsByCategory function with the selected category
    fetchNewsByCategory(category);
  };

  const handleSearch = (searchText) => {
    searchByText(searchText);
  }

  const handleShowProfile = () => {
    setShowProfile(true);
  };

  const handleLogout = () => {
    setLogout();
  };  


  return (
    <nav className="navbar navbar-expand-lg navbar-light bg-light">
      <div className="container-fluid">
        <button
          className="navbar-toggler"
          type="button"
          data-mdb-toggle="collapse"
          data-mdb-target="#navbarSupportedContent"
          aria-controls="navbarSupportedContent"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <i className="fas fa-bars"></i>
        </button>

        <div className="collapse navbar-collapse" id="navbarSupportedContent">
          {/* <a className="navbar-brand mt-2 mt-lg-0" href="#">
            <img
              src="https://mdbcdn.b-cdn.net/img/logo/mdb-transaprent-noshadows.webp"
              height="15"
              alt="MDB Logo"
              loading="lazy"
            />
          </a> */}

          <ul className="navbar-nav me-auto mb-2 mb-lg-0">
            {
                props.categories.filter(cat => !preferences || (preferences && preferences.category_id.includes(cat.id))).map((category) => (
                  <li className="nav-item" key={category.id}>
                    <a className="nav-link" onClick={() => handleCategoryClick(category.id)}>
                      {category.name.toUpperCase().slice(0,1) + category.name.toLowerCase().slice(1,category.name.length) }
                    </a>
                  </li>
                ))
            }
          </ul>
        </div>

        <div className="d-flex align-items-center">
        { authData.signedIn && authData.user  (
          <div className="dropdown">
            <a
              className="dropdown-toggle d-flex align-items-center hidden-arrow"
              id="navbarDropdownMenuAvatar"
              role="button"
              data-mdb-toggle="dropdown"
              aria-expanded="false"
            >
              <img
                src={image}
                className="rounded-circle"
                height="25"
                alt={authData.user.name}
                loading="lazy"
              />
            </a>
            <ul
              className="dropdown-menu dropdown-menu-end"
              aria-labelledby="navbarDropdownMenuAvatar"
            >
              <li>
                <a className="dropdown-item" onClick={handleShowProfile}>
                  My profile
                </a>
              </li>
              <li>
                <a className="dropdown-item" onClick={handleLogout}>
                  Logout
                </a>
              </li>
            </ul>
          </div>
        )}
        {showProfile && <ProfileModal showProfileModal={showProfile} setShowProfile={setShowProfile} props={props}/>}
        </div>
      </div>
    </nav>

  );
}

export default Navbar;
